/**
 * 用于图片服务相对路径的指向
 * @author fanyonglong
 */
import React, { useMemo, useState, useEffect, useCallback } from 'react';
import { Spin, Image as AntImage, ImageProps } from 'antd';
import app from '@/utils/app';
interface ImageViewProps extends ImageProps {
  src: string;
  srcSuffix?: string;
  fillDomainPrefix?: boolean; // 是否填充域名
}

export let ImageView: React.FC<ImageViewProps> = ({
  src,
  preview: propPreview = true,
  srcSuffix = '',
  fillDomainPrefix = true,
  fallback = 'data:image/png;base64,',
  ...restProps
}) => {
  let imgUrl = useMemo(() => {
    return fillDomainPrefix
      ? src.startsWith('http')
        ? src
        : app.toImageUrl(src)
      : src;
  }, [src, fillDomainPrefix]);
  let preview = useMemo<any>(() => {
    if (typeof propPreview == 'boolean') {
      return propPreview == true ? { src: imgUrl } : propPreview;
    }
    return propPreview;
  }, [propPreview]);

  return (
    <AntImage
      preview={preview}
      src={imgUrl + srcSuffix}
      fallback={fallback}
      {...restProps}
    ></AntImage>
  );
};
type ImageGroupPreviewProps={
  images:string[]
  visible:boolean
  onVisibleChange?:(visible:boolean,preVisible:boolean)=>void
  privewProps?:any
}
export let ImageGroupPreview:React.FC<ImageGroupPreviewProps>=(props)=>{
   let {images,visible,onVisibleChange,privewProps}=props
   return  <div style={{ display: 'none' }}>
   <AntImage.PreviewGroup  preview={{...(privewProps||{}), visible:visible, onVisibleChange: (visible,preVisible) =>{
       if(onVisibleChange){
          onVisibleChange(visible,preVisible)
       }
   } }}>
   {images.map((src,index)=><ImageView key={index+src} src={src} />)}
   </AntImage.PreviewGroup>
</div>
}
export let PreviewGroup = AntImage.PreviewGroup;
